import React, { useState } from "react";
import ToDoItem from "./ToDoItem";
import InputArea from "./InputArea";
import EditItem from './EditItem';

function App() {


  const [items, setItems] = useState([]);



  function addItem(inputText) {
    setItems( (prevItems) => {
      return [...prevItems, inputText]
    } )
  }

  function deleteItem(id) {
      setItems( (prevItems) => {
        return prevItems.filter(
          (item, index) => {
            return index !== id
          }
        )
      } )
  }

  function editItem(setIsEditing){
    setIsEditing(prevEdit => {
      if(!prevEdit){
        return prevEdit('Save')
      } else {
        return prevEdit('Edit')
      }
    })
  }

  function resetTodo(){
    setItems(prevItems => prevItems = [])
  }

  return (
    <div className="container">
      <div className="heading">
        <h1>To-Do List</h1>
      </div>

      <InputArea onAdd={addItem} onReset={resetTodo} />

      <div>
        <ul>
          {
            items.map( (todoItem, index) => {
              if(todoItem === ''){
                return
              } else {
                return (
                  <div className='items-container'>
                    <ToDoItem key={index} id={index} text={todoItem} onChecked={deleteItem} />
                    <EditItem id={index} onEdit={editItem} />
                  </div>
                )
              }
            })
          }
        </ul>
      </div>
    </div>
  );
}

export default App;
