import React, { useState } from "react";
import ToDoItem from "./ToDoItem";
import InputArea from "./InputArea";
import EditToDoItem from './EditToDoItem';


function App() {


  const [items, setItems] = useState([]);

  function addItem(item) {
    setItems(prevItems => {
      if(item === ''){
        return [...prevItems]
      } else {
        return [...prevItems, {text: item, isCompleted: false, isEdited: false}]
      }
    })
    console.log(items)
  }

  function resetItems(){
    setItems(prevItems => prevItems = [])
  }

  function deleteItem(id) {
    setItems(prevItems => {
      return prevItems.filter((item, index) => {
        return index !== id
      })
    })
  }

  function completeItem(id) {
    setItems(prevItems => {
      return prevItems.map((item, index) => {
        return index === id ? {...item, isCompleted: !item.isCompleted} : item
      })
    })
  }

  function editItem(id) {
    setItems(prevItems => {
      return prevItems.map((item, index) => {
        return index === id ? {...item, isEdited: !item.isEdited} : item
      })
    })
  }

  function addEditItem(value, id) {
    setItems(prevItems => {
      return prevItems.map((item, index) => {
        return index === id ? {...item, text: value, isEdited: !item.isEdited} : item;
      })
    })
    console.log(items)
  }

  return (
    <div className="container">
      <div className="heading">
        <h1>To-Do List</h1>
      </div>

      <InputArea onAdd={addItem} onReset={resetItems} />

      <div>
          {items.map((item, index) => {
            return item.isEdited ? (
              <ul>
                <EditToDoItem onAddEditItem={addEditItem} item={item} id={index} key={index} />
              </ul>
            ) : <ToDoItem key={index} id={index} item={item} onDel={deleteItem} onComplete={completeItem} onEdit={editItem} />
          })}         
      </div>
    </div>
  );
}

export default App;
