import React, {useState} from 'react';

function EditItem(props){

    const [isEditing, setIsEditing] = useState(false)

    return (
        <div>
            <button onClick={() => {
                props.onEdit(isEditing, setIsEditing)
            }}>
                <span>{isEditing}</span>
            </button>
        </div>
    )
}

export default EditItem;
