import React, {useState} from 'react';

function EditItem(){

    const [isEditing, setIsEditing] = useState('Edit')

    function isEditOn(){
        setIsEditing(prevEdit => {
            if(prevEdit === 'Edit'){
                return 'Save'
            } else if(prevEdit === 'Save'){
                return 'Edit'
            }
        })
    }

    return (
        <div>
            <button onClick={isEditOn}>
                <span>{isEditing}</span>
            </button>
        </div>
    )
}

export default EditItem;
