import React, {useState} from 'react';
import ToDoItem from './ToDoItem';

function EditItem() {

    const [isEdit, setIsEdit] = useState('Edit')

    function handleEdit(){
        setIsEdit(prevEdit => {
            if(prevEdit === 'Edit'){
                return 'Save'
            } else {
                return 'Edit'
            }
        })
    }

    return (
        <button onClick={handleEdit}>
            <span>{isEdit}</span>
        </button>
    )
}

export default EditItem;
