import React, { useState } from "react";
import ToDoItem from "./ToDoItem";
import InputArea from "./InputArea";

function App() {


  const [items, setItems] = useState([]);

  function addItem(item) {
    setItems(prevItems => {
      if(item === ''){
        return [...prevItems]
      } else {
        return [...prevItems, {text: item, isCompleted: false, isEdited: false}]
      }
    })
    console.log(items)
  }

  function resetItems(){
    setItems(prevItems => prevItems = [])
  }

  function deleteItem(id) {
    setItems(prevItems => {
      prevItems.filter((item, index) => {
        return index !== id
      })
    })
  }

  return (
    <div className="container">
      <div className="heading">
        <h1>To-Do List</h1>
      </div>

      <InputArea onAdd={addItem} onReset={resetItems} />

      <div>
        <ul>
          {items.map((item, index) => {
            return <ToDoItem key={index} text={item} onDel={deleteItem} />
          })}
        </ul>
      </div>
    </div>
  );
}

export default App;
