import React, {useState} from 'react';

function ToDoItem(props) {

    const [items, setItems] = useState([]);

    function deleteItem(id) {
        setItems( (prevItems) => {
          return prevItems.filter(
            (item, index) => {
              return index !== id
            }
          )
        } )
    }

    return ( 
        <div>
            <div items={props.items} onItems={props.onItems}>
                <li>{props.text}</li>
            </div>
        </div>
    )
}


export default ToDoItem;
