function EditToDoItem() {
    return (
        <div className="form">
            <form onSubmit={(e) => {
                e.preventDefault();
                setInputText('')
                }}>
                <input onChange={handleChange} value={inputText} />
                    <button type="submit">
                        <span>Edit</span>
                    </button>
            </form>
                <button onClick={() => {
                setInputText('');
                onReset();
                }}>
                <span>Reset To-do</span>
                </button>
    </div>
    )
}

export default EditToDoItem;
